pragma solidity ^0.8.10;

import "./BreadToken.sol";

contract CrumbsToken {
    string public name = "Crumbs Contract";
    BreadToken public breadToken;

    constructor(BreadToken _breadToken) {
        breadToken = _breadToken;
    }
}
